import React from 'react'
import styled from 'styled-components'
import { LoginForm } from './LoginForm';

//container
const BoxContainer = styled.div`
    width: 280px;
    min-height: 550px;
    display: flex;
    flex-direction: column;
    border-radius: 19px;
    background-color: #ffff;
    box-shadow: 0 0 2px rgba(15, 15, 15, 0.28);
    position: relative;
    overflow: hidden;
`;

const TopContainer = styled.div`
    width: 100%;
    height: 150px;
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    padding: 0 1.8em;
    padding-bottom: 5em;
`;

//the bubble coming down
const BackDrop = styled.div`
    width: 160%; // the drop
    height: 550px;
    position: absolute;
    display: flex;
    flex-direction: column;
    border-radius: 50%;
    transform: rotate(60deg);
    top: -290px;
    left: -70px;
    background: rgb(0,61,255);
    background: linear-gradient(90deg, rgba(0,61,255,1) 0%,
                 rgba(0,169,156,1) 49%,
                 rgba(0,97,50,1) 100%);

`;

// Header Text Container
const HeaderContainer = styled.div`
    width: 100%;
    display: flex;
    flex-direction: column;

`;

//Welcome Back text
const HeaderText = styled.h2`
    font-size: 30px;
    font-weight: 600;
    line-height: 1.24;
    color: #ffff;
    z-index: 10;
    margin: 0;
`;

//sign up container
const SmallText = styled.h5`
  color: #fff;
  font-weight: 500;
  font-size: 11px;
  z-index: 10;
  margin: 0;
  margin-top: 7px;
`;

const InnerContainer = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  padding: 0 1.8em;
`;


function AccountBox(props) {
    return (
        <BoxContainer>
            <TopContainer>
                <BackDrop/>
                <HeaderContainer>
                    <HeaderText>Welcome</HeaderText>
                    <HeaderText>Back</HeaderText>
                    <SmallText>Please sign-up to continue!</SmallText>
                </HeaderContainer>
            </TopContainer>
            <InnerContainer>
                <LoginForm/>
            </InnerContainer>
        </BoxContainer>
    )
}

export default AccountBox
