import React from 'react'
import styled from 'styled-components'

//container
const BoxContainer = styled.div`
    width: 200px;
    min-height: 550px;
    display: flex;
    flex-direction: column;
    border-radius: 19px;
    background-color: #ffff;
    box-shadow: 0 0 2px rgba(15, 15, 15, 0.28);
    position: relative;
    overflow: hidden;
`;

const TopContainer = styled.div`
    width: 100%;
    height: 250px;
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    padding: 0 1.8em;
    padding-bottom: 5em;
`;

//the bubble coming down
const BackDrop = styled.div`
    width: 160%; // the drop
    height: 550px;
    position: absolute;
    display: flex;
    flex-direction: column;
    border-radius: 50%;
    top: -290px;
    left: -50px;
    background: rgb(0,61,255);
    background: linear-gradient(90deg, rgba(0,61,255,1) 0%,
                 rgba(0,169,156,1) 49%,
                 rgba(0,97,50,1) 100%);

`;

function AccountBox(props) {
    return (
        <BoxContainer>
            <TopContainer>
                <BackDrop/>
            </TopContainer>
        </BoxContainer>
    )
}

export default AccountBox
